    public static Bitmap getBitmap(Drawable drawable){
        try {
            Bitmap bitmap;

            bitmap = Bitmap.createBitmap(drawable.getIntrinsicWidth(), drawable.getIntrinsicHeight(), Bitmap.Config.ARGB_8888);

            Canvas canvas = new Canvas(bitmap);
            drawable.setBounds(0, 0, canvas.getWidth(), canvas.getHeight());
            drawable.draw(canvas);
            return bitmap;
        } catch (OutOfMemoryError e) {
            // Handle the error
            return null;
        }
    }
    public static boolean isEmailValid(String correo) {
        Matcher matcher = VALID_EMAIL_ADDRESS_REGEX.matcher(correo);
        return matcher.matches();
    }
    public static int getAge(Date bday) {
        Calendar a = Calendar.getInstance();
        a.setTime(bday);
        Calendar b = Calendar.getInstance();
        int diff = b.get(YEAR) - a.get(YEAR);
        if (a.get(MONTH) > b.get(MONTH) || (a.get(MONTH) == b.get(MONTH) && a.get(DATE) > b.get(DATE))) {
            diff--;
        }
        return diff;
    }
    public static LatLng midPoint(LatLng point1, LatLng point2){
        double latitude = point1.latitude+point2.latitude;
        double longitude = point1.longitude+point2.longitude;
        return new LatLng(latitude/2, longitude/2);
    }
    public static String longToString(long milliseconds){
        int seconds = (int) (milliseconds / 1000) % 60 ;
        int minutes = (int) ((milliseconds / (1000*60)) % 60);
        int hours   = (int) ((milliseconds / (1000*60*60)) % 24);
        String time = hours+":"+minutes+":"+seconds;
        return String.format("%02d:%02d:%02d", hours, minutes, seconds);
    }
